      <ModalExample />




      {/* <Sitebar />
      <br></br>

      <Header />
      <br></br>

      <SearchPlants />
      <br></br>
      <Footer />


    <Footer /> */}
      <Auth updateToken={updateToken}/>


      <GardenIndex token={sessionToken}/>







      {/* <SearchPlants /> */}
      <PlantsIndex token={sessionToken}/>




    </div>
  );
}

export default App;
